"""
Fetches students of a college based on roll no of one student.
"""

# roll no of a college student
CONST_college_roll_no = "1723901"


# constant POST parameters per college
CONST_VIEWSTATE = """"""
CONST_EVENTVALIDATION = """"""



import requests
from bs4 import BeautifulSoup

r= requests.get('http://duexam2.du.ac.in/RSLT_ND2017/Students/List_Of_Students.aspx?StdType=REG&ExamFlag=PG_SEMESTER_2Y&CourseCode=822&CourseName=(P.G)-M.Sc.%20COMPUTER%20SCIENCE&Part=I&Sem=I')
soup = BeautifulSoup(r.text, 'html.parser')
students_table = soup.find("table", {"rules": "all"})
data = []
all_students = students_table.find_all('tr')

for student in all_students:
    cols = student.find_all('td')
    cols = [ele.text.strip() for ele in cols]
    data.append([ele for ele in cols if ele]) # Get rid of empty values


data[0] = ['sno','srollno','sname','sfathername']
data.pop(0)
